import React, { useState, useRef } from "react";
// import { Link } from "react-router-dom";
import "./Card.css";

export default function Card() {
  const [xRotation, setXRotation] = useState(0);
  const [yRotation, setYRotation] = useState(0);
  const cardRef = useRef(null);

  function handleMouseMove(event) {
    const card = cardRef.current;
    const { offsetWidth: width, offsetHeight: height } = card;
    const { clientX, clientY } = event;
    const x = clientX - card.offsetLeft - width / 2;
    const y = clientY - card.offsetTop - height / 2;
    var mult = 40;
    setXRotation((y / height) * mult);
    setYRotation((x / width) * mult);
  }

  return (
    <a href="https://www.aresesylvester.com" exact>
      <div
        className="card"
        ref={cardRef}
        style={{
          transform: `rotateX(${xRotation}deg) rotateY(${yRotation}deg)`,
        }}
        onMouseMove={handleMouseMove}
      >
        <img
          src={require("./images/arese.png")}
          alt="Arese Sylvester's Website"
          className="card--img"
        />
        <h1 className="card--title" ref={titleRef}>
          Arese Sylvester
        </h1>
        <p ref={descRef}>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Voluptatum
          eos fugit fugiat doloribus totam corrupti laboriosam mollitia eligendi
          tempora corporis.
        </p>
      </div>
    </a>
  );
}
